library("lme4"); library(tidyverse); nest <- read_rds('Data/active/success-cleaned.rds') %>% mutate(pcpafter_std_gridmet_sq = pcpafter_std_gridmet * pcpafter_std_gridmet)
mod<-glmer(formula = at_least_one_success ~ Tmax_std_gridmet * NewLU1 + Tmax_std_gridmet_sq * NewLU1 + pcpafter_std_gridmet * NewLU1 + pcpafter_std_gridmet_sq * NewLU1 + NLCD_p_forest + NLCD_p_human + NLCD_p_ag + substrate_binary + laydate_scaled + (1 | species) + (1 | year) + (1 | Region/UnCoor), data = nest, family = binomial(link = "logit"), control = glmerControl(optimizer = "bobyqa", optCtrl = list(maxfun = 2e+05)))
write_rds(mod,"results/revisions/mainv1_luxpafter_anom.rds")
